<?phpfunction shopwise_add_elementor_page_settings_controls( $page ) {	$page->add_control( 'shopwise_elementor_hide_page_header',		[			'label'          => esc_html__( 'Hide Header', 'shopwise' ),            'type'           => \Elementor\Controls_Manager::SWITCHER,			'label_on'       => esc_html__( 'Yes', 'shopwise' ),			'label_off'      => esc_html__( 'No', 'shopwise' ),			'return_value'   => 'yes',			'default'        => 'no',		]	);	$page->add_control( 'shopwise_elementor_page_header_type',		[			'label' => esc_html__( 'Header Type', 'shopwise' ),			'type' => \Elementor\Controls_Manager::SELECT,			'default' => '',			'options' => [				'' => esc_html__( 'Select a type', 'shopwise' ),				'type1' 	  => esc_html__( 'Type 1', 'shopwise' ),				'type2'		  => esc_html__( 'Type 2', 'shopwise' ),				'type3'		  => esc_html__( 'Type 3', 'shopwise' ),				'type4'		  => esc_html__( 'Type 4', 'shopwise' ),				'type5'		  => esc_html__( 'Type 5', 'shopwise' ),			],		]	);	$page->add_control( 'shopwise_elementor_hide_page_footer',		[			'label'          => esc_html__( 'Hide Footer', 'shopwise' ),			'type'           => \Elementor\Controls_Manager::SWITCHER,			'label_on'       => esc_html__( 'Yes', 'shopwise' ),			'label_off'      => esc_html__( 'No', 'shopwise' ),			'return_value'   => 'yes',			'default'        => 'no',		]	);		if(get_theme_mod('shopwise_footer_subscribe_area') == 1){		$page->add_control( 'shopwise_elementor_hide_subscribe_form',			[				'label'          => esc_html__( 'Hide Subscribe Form', 'shopwise' ),				'type'           => \Elementor\Controls_Manager::SWITCHER,				'label_on'       => esc_html__( 'Yes', 'shopwise' ),				'label_off'      => esc_html__( 'No', 'shopwise' ),				'return_value'   => 'yes',				'default'        => 'no',			]		);	}		$page->add_control( 'shopwise_elementor_logo',		[			'label'          => esc_html__( 'Set Logo', 'shopwise' ),            'type' 			 => \Elementor\Controls_Manager::MEDIA,		]	);		$page->add_control(		'page_width',		[			'label' => __( 'Width', 'plugin-domain' ),			'type' => \Elementor\Controls_Manager::SLIDER,			'devices' => [ 'desktop' ],			'size_units' => [ 'px'],			'range' => [				'px' => [					'min' => 1100,					'max' => 1650,					'step' => 5,				],			],			'default' => [				'unit' => 'px',				'size' => 1140,			],			'selectors' => [				'{{WRAPPER}} .container' => 'max-width: {{SIZE}}{{UNIT}};',				'{{WRAPPER}} .elementor-section.elementor-section-boxed>.elementor-container' => 'max-width: {{SIZE}}{{UNIT}};',			],								]	);}add_action( 'elementor/element/wp-page/document_settings/before_section_end', 'shopwise_add_elementor_page_settings_controls' );